import React from 'react';
import { Card,CardActions,CardContent,CardMedia,Button,Typography } from '@mui/material';
import ThumbUp from './icons/thumbs-up-solid.svg';
import Delete from './icons/trash-solid.svg';
import Menu from './icons/ellipsis-solid.svg';
import moment from 'moment';

const Post = ({post})=>{
    return(
        <Card className="card">
            <CardMedia className="media" image={post.selectedFile} title={post.title}/>
            <div className="overlay">
                <Typography variant="h6">{post.creator}</Typography>
                <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
            </div>
            <div className='overlay2'>
                <Button style={{color:'white'}} size="small" onClick={()=>{}}>
                    <img src={Menu} alt="Menu" className="menu"/>
                </Button>
            </div>
            <div className='details'>
                <Typography variant="body2" color="textSecondary">{post.tags.map((tag)=>`#${tag}`)}</Typography>
            </div>
            <CardContent>
                <Typography className="title" variant="h5" gutterBottom>{post.message}</Typography>
            </CardContent>
            <CardActions className='cardActions'>
                <Button size="small" color="primary" onClick={()=>{}}>
                    <img src={ThumbUp} alt="thumbup" className='thumb' />
                    {/* <ThumbUp icon="solid thumbsup" style={{color: "#10f1f1"}}/><ThumbUp icon="solid thumbsup" bounce /> */}
                    Like
                    {post.likeCount}
                </Button>
                <Button size="small" color="primary" onClick={()=>{}}>
                    <img src={Delete} alt="delete" className='delete'/>
                    Delete
                </Button>
            </CardActions>
        </Card>

    );
}
export default Post;
