import React from 'react';
import {Container,AppBar,Typography,Grow,Grid} from '@mui/material';
import memory from './images/memory.jpg';
import Posts from './components/posts/posts';
import Forms from './components/forms/form';
import {useState,useEffect} from 'react';
import {useDispatch} from 'react-redux'
import {getPosts} from './actions/posts';

import "./styles.css";
{/* <meta name="viewport" content="width=device-width, initial-scale=1.0"/> */}

const App=()=>{
    const [currentID,setCurrentId]=useState(null);
    // const classes=useStyles();
    const dispatch = useDispatch();
    useEffect(()=>{ dispatch(getPosts());},[currentID,dispatch]);
    return(
        <Container maxWidth="lg">
            <AppBar className="appBar" position="static" color="inherit">
                <Typography className="heading" variant="h2" align="center">Memories</Typography>
                <img className="image" src={memory} alt="memories"/>
            </AppBar>
            <Grow in>
                <Container>
                    <Grid className="mainContainer" container justify="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts setCurrentId={setCurrentId}/>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Forms currentID={currentID} setCurrentId={setCurrentId}/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
        </Container>
    );
}
export default App;
